        public ActionResult Activated(int userid, string code, string Url)
        {
            var user = _context.Users.Where(u => u.Id == userid).FirstOrDefault();
            user.IsActive = true;
            _context.Update(user);
            _context.SaveChanges();
            HttpContext.Session.SetString("wrong", "your email is verified please login to continue");

            return RedirectToAction("login");
        }
        private async Task<bool> SendConfirmationEmail(string email, string subject, string confirmlink)
        {

            try
            {
                MailMessage message = new MailMessage();
                System.Net.Mail.SmtpClient smtpClient = new System.Net.Mail.SmtpClient();
                message.From = new MailAddress("chpanchal2447@gmail.com");
                message.To.Add(email);
                message.Subject = subject;
                message.IsBodyHtml = true;
                message.Body = confirmlink;


                smtpClient.Port = 587;
                smtpClient.Host = "smtp.gmail.com";

                smtpClient.EnableSsl = true;
                smtpClient.UseDefaultCredentials = false;
                smtpClient.Credentials = new NetworkCredential("chpanchal2447@gmail.com", "ciujvngndoigfgjy");
                smtpClient.DeliveryMethod = SmtpDeliveryMethod.Network;
                smtpClient.Send(message);
                return true;
            }
            catch (Exception)
            {

                return false;
            }
        }
        public ActionResult Login()
        {
            var referer = Request.Headers["Referer"].ToString();
            ViewData["ReturnUrl"] = referer;
